    % measurement model
%     h = [- fx*y(5) - (fx*(x(4)*(x(7)^2 + x(8)^2 - x(9)^2 - x(10)^2) + x(5)*(2*x(7)*x(10) + 2*x(8)*x(9)) - x(6)*(2*x(7)*x(9) - 2*x(8)*x(10)))*(x(7)^2 - x(8)^2 - x(9)^2 + x(10)^2))/height;...
